import { useEffect, useState } from "react";

const StackCard = () => {
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setInView(true);
          }
        });
      },
      { threshold: 0.3 }
    );

    const cardElement = document.querySelector(".stack-card");
    if (cardElement) observer.observe(cardElement);

    return () => {
      if (cardElement) observer.unobserve(cardElement);
    };
  }, []);

  return (
    <div
      className={`stack-card w-64 h-80 p-6 rounded-lg shadow-lg transition-all duration-700 transform ${
        inView ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
      }`}
      style={{ backgroundColor: "#E6E6FA" }} // Lavender color
    >
      <h2 className="text-2xl font-semibold mb-4">Stack Card Title</h2>
      <p className="text-lg">
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam euismod
        lorem vel justo fermentum.
      </p>
    </div>
  );
};

const StackCardContainer = () => {
  return (
    <div className="flex justify-center items-center space-x-6 mt-16">
      <StackCard />
      <StackCard />
      <StackCard />
      <StackCard />
    </div>
  );
};

export default StackCardContainer;
