import { useEffect, useState } from "react";

const StackCard = ({ inView }) => {
  return (
    <div
      className={`w-64 h-80 p-6 rounded-lg shadow-lg mb-6 transition-all duration-700 transform ${
        inView
          ? "opacity-100 translate-y-0"
          : "opacity-0 translate-y-10"
      }`}
      style={{ backgroundColor: "#E6E6FA" }} // Lavender color
    >
      <h2 className="text-2xl font-semibold mb-4">Stack Card Title</h2>
      <p className="text-lg">
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam euismod
        lorem vel justo fermentum.
      </p>
    </div>
  );
};

const StackCardContainer = () => {
  const [inView, setInView] = useState([false, false, false, false]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry, index) => {
          if (entry.isIntersecting) {
            setInView((prev) => {
              const newInView = [...prev];
              newInView[index] = true;
              return newInView;
            });
          }
        });
      },
      { threshold: 0.3 }
    );

    const cardElements = document.querySelectorAll(".stack-card");
    cardElements.forEach((card) => observer.observe(card));

    return () => {
      cardElements.forEach((card) => observer.unobserve(card));
    };
  }, []);

  return (
    <div className="flex flex-col items-center mt-16">
      {[...Array(4)].map((_, index) => (
        <div
          key={index}
          className="stack-card"
          style={{ width: "16rem", height: "20rem" }}
        >
          <StackCard inView={inView[index]} />
        </div>
      ))}
    </div>
  );
};

export default StackCardContainer;
